__memory_dict = {}


def __setDictionary(key, value):
    global __memory_dict
    try:
        __memory_dict.update({key: value})
        # executionLogger.info(f"dict updated with {key}:{value}")
    except Exception as e:
        # executionLogger.warning(f"Exception occurred setting Dictionary values. {e}")
        print(f"Exception occurred setting Dictionary values. {e}")

    #print("Updated dict is: {0}".format(memory_dict))


def __getDictionary(key=None):
    try:
        if key != None:
            return __memory_dict[key]
        return __memory_dict
    except Exception as e:
        # executionLogger.warning(f"Exception occurred getting Dictionary values. {e}")
        print(f"Exception occurred getting Dictionary values. {e}")


def __loadDictionary(data=dict):
    try:
        global __memory_dict
        __memory_dict=data
    except Exception as e:
        # executionLogger.warning(f"Exception occurred getting Dictionary values. {e}")
        print(f"Exception occurred loading Dictionary values. {e}")
