import subprocess
import sys

def is_package_installed(package):
    try:
        installed_packages = subprocess.check_output([sys.executable, "-m", "pip", "list"]).decode()
        return package.lower() in installed_packages.lower()
    except subprocess.CalledProcessError:
        print("Error checking installed packages.")

# Function to check if a package is installed
def install_package(package):
    if is_package_installed(package):
        print(f"{package} is already installed.")
    else:
        try:
            print(f"{package} not found. Installing now...")
            subprocess.check_call([sys.executable, "-m", "pip", "install", package])
            print(f"{package} is installed.")

        except subprocess.CalledProcessError:
            print(f"{package} could not be installed...")
           