from ..utils import utils


class DdtHelperClass:
    def __init__(self, file_name, user_path=''):
        self.file_name = file_name
        self.user_path = user_path
        output_data = {}
        self.file_path = utils.findDataPath(self.file_name, self.user_path)

    def readJson(self):
        jsonData = utils.read_json(self.file_path)
        # print("The JSON is {0}".format(jsonData))
        return dict(jsonData)